INSERT INTO public."Review" VALUES (15928, 20, 7, 8.50, '2024-11-02', 'expert_overall', 'I enjoyed playing in the Nike Ja 2. It''s Kobe-esque, but there are other better-cushioned options available. The traction plays well, and I like the snug fit.');
INSERT INTO public."Review" VALUES (15929, 20, 3, 8.40, '2024-11-01', 'expert_overall', 'The Nike Ja 2 is one of the most practical, no-BS Nike shoes. The shoe''s strengths are traction, weight, and agility.');
