INSERT INTO public."Review" VALUES (15935, 20, 10, 7.80, '2024-10-16', 'expert_overall', 'I prefer the Nike Ja 1 because of the fit, but the Ja 2 provides a softer, minimal upper. The comfort feels decent, and the traction performs well.');
