INSERT INTO public."Review" VALUES (16482, 30, 32, 9.00, '2024-08-07', 'traction', 'The outsole performs well in most situations. The rubber is a dust magnet, though. If you wipe, it doesn''t affect performance much.');
INSERT INTO public."Review" VALUES (16483, 30, 5, 8.00, '2024-08-27', 'traction', 'The traction performance is better than expected. It''s not fantastic, but it''s not bad. The outsole is a dust magnet, though.');
INSERT INTO public."Review" VALUES (16484, 30, 6, 8.00, '2024-08-19', 'traction', 'The traction performance is decent. I had no issues with clean courts, but dustier courts caused problems. This outsole is a dust magnet.');
