INSERT INTO public."Review" VALUES (16705, 34, 3, 9.00, '2024-09-21', 'expert_overall', 'The Way of Wade All City 12 Encore is an elite performer. Traction and cushioning are both top-tier, and the materials even feel comfortable.');
