INSERT INTO public."Review" VALUES (16709, 34, 22, 8.50, '2024-09-21', 'expert_overall', 'The Way of Wade All City 12 Encore is an impressive all-around performer. The shoe has no real weak points. The cushioning is balanced, and the traction performance is great.');
