INSERT INTO public."Review" VALUES (16799, 36, 10, 9.00, '2024-08-03', 'expert_overall', 'The Li-Ning Way of Wade 11 is an incredible performance shoe. The traction and cushioning combination is outstanding. My only complaints are the slight bulkiness and lack of ventilation.');
