INSERT INTO public."Review" VALUES (17037, 39, 7, 9.50, '2024-07-19', 'materials', 'I enjoy the materials on the shoe. It''s a mesh upper that feels strong and breathable. There was no break-in time, and I felt comfortable right away.');
