INSERT INTO public."Review" VALUES (17124, 41, 1, 8.00, '2024-08-06', 'materials', 'The upper has so much going on. The elastic strap helps with containment, and the tongue gusset is aggressive. The materials are TPU and textiles. It feels comfortable.');
