INSERT INTO public."Review" VALUES (17269, 45, 7, 7.00, '2024-05-03', 'traction', 'The traction pattern is the same as the standard Zion 3. The performance works on clean courts, but dirty floors cause problematic sliding.');
INSERT INTO public."Review" VALUES (17270, 45, 6, 7.00, '2024-05-03', 'traction', 'The traction performance is not great. It only plays well on clean courts.');
