INSERT INTO public."Review" VALUES (17539, 49, 19, 8.00, '2024-06-14', 'traction', 'I doubted the traction at first. The grip feels solid on clean hardwood.');
INSERT INTO public."Review" VALUES (17540, 49, 1, 6.00, '2024-03-17', 'traction', 'There''s a lot of ground contact for the traction, but the outsole picks up and holds on to dust faster than any other shoe. I enjoy the profile if you can keep the dust off.');
