INSERT INTO public."Review" VALUES (17551, 49, 6, 8.00, '2024-03-17', 'cushion', 'The Air Strobel is comfortable. It provides a good balance between responsiveness and impact protection.');
INSERT INTO public."Review" VALUES (17552, 49, 103, 7.00, '2024-05-03', 'cushion', 'The cushioning is low to the ground with tons of court feel. The impact protection is decent, but the shoe is stiff.');
INSERT INTO public."Review" VALUES (17553, 49, 3, 9.00, '2024-05-01', 'materials', 'The materials are not premium, but the way the shoe is constructed is incredibly comfortable. The shoe''s build caters to what I like in a shoe.');
INSERT INTO public."Review" VALUES (17554, 49, 10, 8.00, '2024-03-17', 'materials', 'I like the use of materials. It''s supportive and sturdy. The shoe is incredibly light, too.');
INSERT INTO public."Review" VALUES (17555, 49, 111, 7.00, '2024-03-17', 'materials', 'The padding feels comfortable. The upper is decent quality, but it causes the shoe to run hot.');
