INSERT INTO public."Review" VALUES (17608, 50, 10, 8.00, '2024-04-05', 'traction', 'The traction performs very well after an initial break-in period. It''s solid, but not top-tier.');
INSERT INTO public."Review" VALUES (17609, 50, 9, 10.00, '2024-05-01', 'traction', 'The traction pattern is unusual. The tread is not aggressive, but it still performs very well. I was pleasantly surprised by how little dust the shoe picked up.');
INSERT INTO public."Review" VALUES (17610, 50, 2, 8.00, '2024-03-17', 'traction', 'The KAI 1 traction is flat but performs decently well.');
INSERT INTO public."Review" VALUES (17611, 50, 32, 8.00, '2024-03-17', 'traction', 'The traction performance feels good but not great.');
INSERT INTO public."Review" VALUES (17612, 50, 8, 9.00, '2024-07-15', 'traction', 'The traction performs very well. It doesn''t pick up much dust, and the grip and bite are impressive.');
INSERT INTO public."Review" VALUES (17613, 50, 5, 8.00, '2024-05-01', 'traction', 'The traction performance is honourable. I was expecting a bit more. I had no issues during play.');
INSERT INTO public."Review" VALUES (17614, 50, 3, 8.00, '2024-05-19', 'traction', 'The traction performance is slightly above average.');
INSERT INTO public."Review" VALUES (17615, 50, 6, 8.00, '2024-06-09', 'traction', 'The traction performance requires some break-in time, but it becomes solid afterwards. Make sure to watch out for dust.');
