INSERT INTO public."Review" VALUES (17719, 52, 7, 8.90, '2024-02-27', 'expert_overall', 'I enjoyed playing in the Nike Book 1. The performance is justified. The traction and materials are standout features.');
INSERT INTO public."Review" VALUES (17720, 52, 111, 8.80, '2024-03-17', 'expert_overall', 'I recommend the Nike Book 1 for every position. I like the traction and cushioning but wish the breathability was better.');
INSERT INTO public."Review" VALUES (17721, 52, 32, 8.40, '2024-02-18', 'expert_overall', 'The Nike Book 1 is a decent basketball shoe. I love the materials and traction performance. The cushioning is slightly lacking, though.');
INSERT INTO public."Review" VALUES (17722, 52, 9, 8.40, '2024-03-22', 'expert_overall', 'I like the Nike Book 1. The performance is best for shooters because of the traction and cushioning setups.');
INSERT INTO public."Review" VALUES (17723, 52, 5, 8.20, '2024-03-09', 'expert_overall', 'The Nike Book 1 is simple and effective. It plays fast with traction which works, and the materials are good quality. My only gripe is with the lack of forefoot cushioning tech.');
