INSERT INTO public."Review" VALUES (17727, 52, 1, 8.00, '2023-12-28', 'expert_overall', 'The Nike Book 1 is incredibly light of foot. It has quick and light side-to-side movement, and the cushioning and traction are impressive for lighter players.');
