INSERT INTO public."Review" VALUES (17760, 52, 9, 9.00, '2024-03-22', 'materials', 'The materials are comfortable. I like how they feel right out of the box, and the padding is soft.');
INSERT INTO public."Review" VALUES (17761, 52, 5, 9.00, '2024-03-09', 'materials', 'Materials are where the sneaker excels. It feels nice on the court, and the quality is impressive.');
