INSERT INTO public."Review" VALUES (17868, 54, 10, 8.00, '2024-03-17', 'materials', 'The materials feel old school. The padding is average but cozy. I appreciate the quality.');
INSERT INTO public."Review" VALUES (17869, 54, 1, 10.00, '2024-03-17', 'support', 'The heel comfort is incredible right off the bat. This is one of the best shoes for support and stability, mainly because of the fit.');
INSERT INTO public."Review" VALUES (17870, 54, 103, 8.00, '2024-04-14', 'support', 'Lockdown is not an issue. I wasn''t slipping within the shoe.');
