INSERT INTO public."Review" VALUES (17973, 56, 7, 9.00, '2024-03-03', 'expert_overall', 'The Air Jordan 38 Low is a well-balanced performer. The support and materials are fantastic, and I love the cushioning setup.');
INSERT INTO public."Review" VALUES (17974, 56, 6, 8.80, '2024-02-05', 'expert_overall', 'I enjoyed playing in the Air Jordan 38 Low. The cushioning is outstanding. The traction performance is solid, too. Make sure to wipe on dust, though.');
