INSERT INTO public."Review" VALUES (18655, 69, 1, 8.00, '2023-10-25', 'fit', 'The fit works best for narrow feet. The reinforcements and shape make the fit cramp for wide-footers.');
INSERT INTO public."Review" VALUES (18656, 69, 5, 8.00, '2023-10-28', 'fit', 'Initially, the fit feels tight and snug. I enjoyed it after breaking in the shoe.');
INSERT INTO public."Review" VALUES (18657, 69, 2, 7.00, '2023-11-11', 'fit', 'The shoe feels narrow. The plastic on the lateral side hugs your foot too much.');
INSERT INTO public."Review" VALUES (18658, 69, 19, 7.00, '2024-05-19', 'fit', 'My foot felt nice in the shoe because of the textured drop-in midsole. The upper construction causes some discomfort. I had to experiment with the laces a lot for a good fit.');
INSERT INTO public."Review" VALUES (18659, 69, 3, 6.00, '2023-12-25', 'fit', 'I think the fit will turn down a lot of players. It feels tight and snug in the toe box, and the width causes pinching and soreness.');
INSERT INTO public."Review" VALUES (18660, 69, 10, 50.00, '2023-11-11', 'outdoor', 'The rubber is good enough for outdoor play.');
INSERT INTO public."Review" VALUES (18661, 69, 7, 50.00, '2023-11-03', 'outdoor', 'The materials seem durable enough for the outdoors, but the shoe is not meant for toe draggers.');
