INSERT INTO public."Review" VALUES (18703, 70, 1, 2.00, '2023-11-17', 'size', 'Go true to size if you have a narrow foot. Go up half a size if you want a more comfortable fit.');
INSERT INTO public."Review" VALUES (18704, 71, 10, 8.20, '2023-12-06', 'expert_overall', 'The Nike LeBron Witness 8 caters to quick play styles. The traction is outstanding, but the cushioning could be better.');
INSERT INTO public."Review" VALUES (18705, 71, 3, 7.80, '2023-12-06', 'expert_overall', 'The Nike LeBron Witness 8 is a solid, budget performer. I think it''s a good pickup if you can get it at a discount. The cushioning is soft, and the traction works.');
INSERT INTO public."Review" VALUES (18706, 71, 22, 7.50, '2023-12-18', 'expert_overall', 'The LeBron Witness 8 is low to the ground and responsive. The materials and support feel better than the previous Witness 7.');
