INSERT INTO public."Review" VALUES (18713, 71, 10, 10.00, '2023-12-06', 'traction', 'The traction is freaking amazing. This is one of the best traction setups in the Witness line. It also bites through dust well.');
INSERT INTO public."Review" VALUES (18714, 71, 3, 8.00, '2023-12-06', 'traction', 'The traction performance is decent.');
INSERT INTO public."Review" VALUES (18715, 71, 22, 8.00, '2023-12-18', 'traction', 'Traction might be the shoe''s best feature. The setup is grippy and offers top-notch directional coverage. It''s not elite, but it is versatile.');
INSERT INTO public."Review" VALUES (18716, 71, 1, 8.00, '2023-12-06', 'traction', 'The outsole tread is my favourite part of the shoe. The pattern grabs courts nicely.');
INSERT INTO public."Review" VALUES (18717, 71, 10, 6.00, '2023-12-06', 'cushion', 'The cushioning setup sucks. Court feel is average, and the impact protection is not good.');
