INSERT INTO public."Review" VALUES (19092, 79, 22, 8.20, '2023-11-25', 'expert_overall', 'The Nike Giannis Immortality 3 is an impressive budget shoe. It''s a solid pick suited well for outdoors. The cushioning and materials are improved.');
