INSERT INTO public."Review" VALUES (19217, 81, 171, 8.80, '2023-11-02', 'user_overall', 'Great shoe overall. The traction is amazing, but picks up dust. Wiping every once in a while is a must. The support really surprised me. My foot was locked in, and it fit perfectly. I highly recommend them.');
