INSERT INTO public."Review" VALUES (20236, 100, 1, 9.00, '2023-04-11', 'cushion', 'I love the snap provided by the shank setup, and the Boom foam is one of my favourites right now. It feels bouncy and comforting.');
INSERT INTO public."Review" VALUES (20237, 100, 10, 9.00, '2023-03-21', 'cushion', 'The full-length Boom cushioning is one of my favourite parts of the shoe. It''s light, soft, and bouncy. It does everything right.');
INSERT INTO public."Review" VALUES (20238, 100, 2, 9.00, '2023-03-21', 'cushion', 'The full-length Boom cushioning is one of my favourites. It''s low to the ground, responsive, and has plenty of impact protection.');
