INSERT INTO public."Review" VALUES (20321, 102, 1, 8.00, '2023-04-29', 'fit', 'The fit feels more streamlined but less plush. It feels like more of a performance fit.');
INSERT INTO public."Review" VALUES (20322, 102, 3, 8.00, '2023-04-13', 'fit', 'I had to loosen the eyelets to customize the fit, but it felt comfortable.');
INSERT INTO public."Review" VALUES (20323, 102, 2, 8.00, '2023-04-13', 'fit', 'The fit feels snug but supportive. The forefoot is narrow, though.');
