INSERT INTO public."Review" VALUES (20325, 102, 1, 100.00, '2023-04-29', 'outdoor', 'The rubber plays very well on outdoor courts.');
INSERT INTO public."Review" VALUES (20326, 102, 3, 100.00, '2023-04-13', 'outdoor', 'These shoes should be good for playing outdoors.');
INSERT INTO public."Review" VALUES (20327, 102, 2, 100.00, '2023-04-13', 'outdoor', 'The rubber is thick and good enough for outdoor use.');
INSERT INTO public."Review" VALUES (20328, 102, 10, 50.00, '2023-04-13', 'outdoor', 'The rubber outsole is thick enough for outdoor use.');
INSERT INTO public."Review" VALUES (20329, 102, 1, 3.00, '2023-04-29', 'width', 'Go true to size if you have a wide foot, and prefer a snug fit. Go up half a size if you prefer a less snug fit.');
