INSERT INTO public."Review" VALUES (20332, 102, 1, 3.00, '2023-04-29', 'size', 'Go true to size if you have a narrow or medium foot.');
INSERT INTO public."Review" VALUES (20333, 102, 3, 2.00, '2023-04-13', 'size', 'I recommend going up half a size.');
INSERT INTO public."Review" VALUES (20334, 102, 2, 3.00, '2023-04-13', 'size', 'I went true to size, but I have a regular foot.');
