INSERT INTO public."Review" VALUES (20711, 112, 18, 9.00, '2023-02-08', 'expert_overall', 'The Li-Ning Way of Wade All City 10 V2 is a fantastic shoe, especially for the price. The cushioning feels perfect, and the traction works. It''s versatile - I recommend this for all positions.');
