INSERT INTO public."Review" VALUES (20758, 113, 22, 8.50, '2023-05-06', 'expert_overall', 'The Adidas Dame Certified is a decent basketball shoe. The traction and material setup are highlights, while the Bounce cushioning is adequate.');
