INSERT INTO public."Review" VALUES (20800, 114, 10, 9.00, '2023-08-01', 'cushion', 'There''s a solid amount of compression in the heel. The bounceback is also impressive while it lasts.');
INSERT INTO public."Review" VALUES (20801, 114, 2, 8.00, '2023-08-01', 'cushion', 'The bounceback from the torsion plate and the foam cushioning improve after an initial break-in period. It''s low and responsive too.');
