INSERT INTO public."Review" VALUES (21477, 127, 111, 9.00, '2022-08-21', 'support', 'The heel slippage on the Cosmic Unity 2 is much improved. I didn''t experience any slippage at all. I loved the lockdown on the shoe too.');
