INSERT INTO public."Review" VALUES (21542, 129, 3, 8.00, '2023-04-13', 'expert_overall', 'The Puma Rise NITRO is a solid performer. It''s not top-tier, but not awful. The stability is the main standout feature, and the traction performs well.');
