INSERT INTO public."Review" VALUES (21767, 134, 293, 7.80, '2022-08-04', 'expert_overall', 'If you''re looking for a lightweight and responsive shoe, the Nike Precision 6 is one of the best you can get for the money. Anybody that needs any cushioning should avoid this shoe, though.');
INSERT INTO public."Review" VALUES (21768, 134, 6, 7.80, '2022-08-04', 'expert_overall', 'I highly recommend the Nike Precision 6 for the price. It should be a solid option for outdoor play and players on a budget.');
