INSERT INTO public."Review" VALUES (21779, 134, 6, 7.00, '2022-08-04', 'cushion', 'You get a bit of compression and bounce back, but it''s more of a court feel and responsiveness shoe.');
INSERT INTO public."Review" VALUES (21780, 134, 307, 8.00, '2022-08-04', 'materials', 'The shoe is lightweight, but the breathability and durability could be better.');
INSERT INTO public."Review" VALUES (21781, 134, 293, 8.00, '2022-08-04', 'materials', 'The Nike Precision 6 uses a soft and comfortable textile material.');
INSERT INTO public."Review" VALUES (21782, 134, 6, 8.00, '2022-08-04', 'materials', 'A textile covers the front half of the shoe, and a mesh covers the back half. I have no complaints performance-wise.');
INSERT INTO public."Review" VALUES (21783, 134, 307, 8.00, '2022-08-04', 'support', 'The support in the shoe performed better than expected.');
INSERT INTO public."Review" VALUES (21784, 134, 293, 9.00, '2022-08-04', 'support', 'I was surprised by how much lateral containment this shoe provided. Lateral stability and ankle security also weren''t an issue.');
