INSERT INTO public."Review" VALUES (21788, 134, 6, 8.00, '2022-08-04', 'fit', 'The shoe has good sculpting around the heel, and there''s no dead space in the toe box. I enjoyed the fit of this shoe.');
INSERT INTO public."Review" VALUES (21789, 134, 307, 50.00, '2022-08-04', 'outdoor', 'The traction didn''t perform as well outdoors as it did indoors.');
INSERT INTO public."Review" VALUES (21790, 134, 293, 100.00, '2022-08-04', 'outdoor', 'You should be good to go for outdoor play.');
