INSERT INTO public."Review" VALUES (28032, 308, 31, 9.00, '2018-03-04', 'traction', 'The traction is awesome!');
INSERT INTO public."Review" VALUES (28033, 308, 615, 9.00, '2018-03-25', 'traction', 'Overall, I give the PG 2s traction a solid thumbs up.');
