INSERT INTO public."Review" VALUES (29400, 337, 31, 8.00, '2017-01-01', 'cushion', 'Good impact protection and very responsive feel.');
INSERT INTO public."Review" VALUES (29401, 337, 293, 7.00, '2017-01-01', 'cushion', 'Impact protection is okay, but it''s not bouncy at all.');
INSERT INTO public."Review" VALUES (29402, 337, 176, 9.00, '2017-01-01', 'materials', 'Honestly, this is one of the most comfortable basketball shoes this season.');
INSERT INTO public."Review" VALUES (29403, 337, 85, 9.00, '2017-01-01', 'materials', 'This is where the shoe shines. An all woven upper, some fuse on high-wear areas, and an external heel counter  plus solid rubber traction for $100.');
INSERT INTO public."Review" VALUES (29404, 337, 755, 9.00, '2017-01-01', 'materials', 'For only $100, the materials are a pretty good bargain that any hooper should be happy with');
