INSERT INTO public."Review" VALUES (29485, 340, 755, 8.20, '2017-01-01', 'expert_overall', 'If youre looking for a cloud-like ride, with great materials and a glove-like fit, the Zoom Rev 17 is going to get the job done really well.');
INSERT INTO public."Review" VALUES (29486, 340, 176, 8.00, '2017-01-01', 'expert_overall', 'The Zoom Rev 17 is an extremely solid option for someone looking for quickness, forefoot cushioning while still riding low, and a great fit.');
