INSERT INTO public."Review" VALUES (29489, 340, 176, 7.00, '2017-01-01', 'traction', 'Tricky, tricky traction. Very good on lateral moves, inconsistent on straight-line starts and stops.');
INSERT INTO public."Review" VALUES (29490, 340, 293, 7.00, '2017-01-01', 'cushion', 'Impact protection is nice, responsiveness is good, but I would like a lower ride.');
