INSERT INTO public."Review" VALUES (29896, 350, 293, 9.00, '2017-01-01', 'support', 'Support is nice, no complaints there. Lockdown is really good too.');
INSERT INTO public."Review" VALUES (29897, 350, 701, 8.00, '2017-01-01', 'support', 'Support is not phenomenal but definitely good.');
INSERT INTO public."Review" VALUES (29898, 350, 85, 10.00, '2017-01-01', 'fit', 'Id say the fit is one of my favorite aspects of the shoe.');
INSERT INTO public."Review" VALUES (29899, 350, 486, 9.00, '2017-01-01', 'fit', 'The overall fit is nothing less than great.');
INSERT INTO public."Review" VALUES (29900, 350, 649, 9.00, '2017-01-01', 'fit', 'The AIr Jordan XXXI provides a sublime fit thats easy to slide on and off, and can be laced as loose or tight as your personal preferences allow.');
