INSERT INTO public."Review" VALUES (29902, 350, 32, 7.00, '2017-01-01', 'fit', 'Some lace adjustments will be needed during your first couple of games to ensure youre getting the best and most secure fit.');
INSERT INTO public."Review" VALUES (29903, 350, 293, 9.00, '2017-01-01', 'fit', 'Fit is so awesome you forget you are wearing shoes.');
